import React, {useState} from 'react'
  
const App = () => { 
const [fullName, setFullName] = useState({
    fname : '',
    lname : ''
})

const inputEvent = (event) => {
  setFullName(event.target.value);
}

const onSubmit = (event) => {
   event.preventDefault();
}

   return (
      <>
      <div className="main_div">
        <form onSubmit={onSubmit}>
          <div>
            <h1>Hello {fullName.fname} {fullName.lname}</h1>
            <input type="text" placeholder="Enter Your First Name" onChange={inputEvent} value={fullName.fname}/>
            <input type="text" placeholder="Enter Your Last Name" onChange={inputEvent} value={fullName.lname}/>
            <button type="submit">Click Me üëç</button>
          </div>
        </form>
      </div>  
      </>
   )
}

export default App;
