import React,{useState} from 'react'
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';


const CreateNote = () => {
    const [note, setNote] = useState({
        title : '',
        content : ''
    })

    const InputEvent = (event) => {

        // const value = event.target.value;
        // const name = event.target.name;

        const {name, value} = event.target;

        setNote((prevData) => {
            return {
                ...prevData
            }
        })
    }
  return (
  <>
     <div className="main_note">
        <form>
           <input type="text" name="title" value={note.title} onChange={InputEvent} placeholder="Title" autocomplete="off"/>
           <textarea rows="" column="content" name="" value={note.content} onChange={InputEvent} placeholder="Write a note"></textarea>
           <Button>
              <AddIcon className="plus_sign"/>
           </Button>
        </form>
     </div>
  </>
  )
}
export default CreateNote;
