import React,{useState} from 'react'
import './currenttime.css'


const CurrTime = () => {
   
   let currTime = new Date().toLocaleTimeString();
   alert(currTime);
   const [curentTime, updatedTime] = useState(currTime)
}

const App = () => {
   return (
     <> 
      <h1>{updatedTime}</h1>
      <button onClick={CurrTime}>Get Time</button>
     </> 
   )
}
export default App;
