import React, {useState} from 'react'

const ChangeTitleOnClick = () => {
    const [state, setstate] = useState(0)

    useEffect(()=>{
        alert('clicked');
    })

    // const changeState = () => {
    //     setstate(state+1)
    // }

    return <button onClick={() => setstate(state+1)}> Click me {state} </button>

}

export default ChangeTitleOnClick
