import React, {useState} from 'react'

const App = () => {

const [count, setCount] = useState(0)

const countInc = () => {
  setCount(count+1)
}

const countDec = () => {
  if(count!='0')
  {
     setCount(count-1)
  }
  else
  {
    alert('you cannot decrease ')
  }
  
}

   return (
     <>
        <div className="main_div">
          <div className="center_div">
          <h1>{count}</h1>
          <div className="btn_div">
            <button onClick={countInc}><i class="fa fa-plus" aria-hidden="true"></i></button>
            <button onClick={countDec}>Decrem</button>
          </div>  
          </div>
        </div>
     </>
   )
}
export default App;
