import React, {useState} from 'react'

const ChangeTitleOnClick = () => {
    const [state, setstate] = useState(6)

    const changeState = () => {
        setstate(state+1)
    }

    return <button onClick={changeState}> Click me {state} </button>

}

export default ChangeTitleOnClick
