import React from 'react'

const App = () => {
   const purple = '#8e44ad';
   const [bg, setBg] = useState(purple)

   const bgChange = () => {
      alert('clicked');
   }

   return (
      <>
      <div style={{ backgroundColor : "red" }}>
        <button onClick={bgChange}> Click Me </button>
      </div>  
      </>
   )
}

export default App;
