import React from 'react'

const App = () => {

  const [item, setItem] = useState("Buy Mabgo")

  const addItem = (event) => {
        setItem(event.target.value)

  } 

  return (
    <>
      <div className="main_div">
         <div className="center_div">
           <br />
           <h1>Todo List</h1>
           <br />
           <input type="text" placeholder="Enter Todo Item" onChange={addItem}/>
           <button> + </button>
           <ol>
             <li>{item}</li>
           </ol>
         </div>
      </div>
    </>
  )

}
export default App;
