import React,{useState} from 'react'

const App = () => {
   const purple = 'yellow';
   const [bg, setBg] = useState(purple)

   const bgChange = () => {
      setBg('red');
   }

   return (
      <>
      <div style={{ backgroundColor : {bg} }}>
        <button onClick={bgChange}> Click Me </button>
      </div>  
      </>
   )
}

export default App;
