const inputEvent = (event) => {
    setName(event.target.value);
}

const onSubmit = () => {
   setFullName(name)
}

   return (
      <>
        <div>
   <h1>Hello {fullName}</h1>
           <input type="text" placeholder="Enter Your Name" onChange={inputEvent} value={name}/>
           <button onClick={onSubmit}>Click Me üëç</button>
        </div>
      </>
   )
}

export default App;
